import React, { useState, useEffect } from 'react';
import './App.css';
import { MovieList } from './components/MovieList/MovieList';
import { OrderByGenre } from './components/OrderBy/OrderByGenre';
import { SearchMovie } from './components/OrderBy/SearchMovie';
import SortBy from './components/OrderBy/SortBy';
import Pagination from './components/Pagination/Pagination';  // Asegúrate de que el archivo exista en esta ubicación

type Movie = {
  id: number;
  title: string;
  release_date: string;
  poster_path: string;
  overview: string;
};

function App() {
  const [peliculas, setPeliculas] = useState<Movie[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [lastAction, setLastAction] = useState<"SEARCH" | "GENRE" | "SORT" | "NONE">("NONE");
  const [totalPages, setTotalPages] = useState<number>(1);

  useEffect(() => {
    const BASE_URL = 'https://api.themoviedb.org/3';
    const API_KEY = '03d8479e6ac8e870c3ef0fea7b1b15c3';

    const fetchData = async () => {
      let endpoint;

      switch (lastAction) {
        case "SEARCH":
          endpoint = `${BASE_URL}/search/movie?query=${busqueda}&api_key=${API_KEY}&page=${currentPage}`;
          break;
        case "GENRE":
          endpoint = `${BASE_URL}/discover/movie?with_genres=${selectedGenre}&api_key=${API_KEY}&page=${currentPage}`;
          break;
        case "SORT":
          endpoint = `${BASE_URL}/discover/movie?sort_by=${sortByValue}&api_key=${API_KEY}&page=${currentPage}`;
          break;
        default:
          return; // En caso de "NONE" o cualquier otro valor inesperado, no hagas nada.
      }

      try {
        const response = await fetch(endpoint);
        const data = await response.json();
        setPeliculas(data.results);
        setTotalPages(data.total_pages);
      } catch (error) {
        console.error("Error fetching data based on action", error);
      }
    };

    fetchData();
  }, [currentPage, lastAction]);

  return (
    <>
      <h1 className='main-title'>Movie Challenge</h1>
      <div className='select-container'>
        <OrderByGenre 
          setPeliculas={setPeliculas} 
          setTotalPages={setTotalPages} 
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
          setLastAction={setLastAction}
        />
        <SortBy 
          setPeliculas={setPeliculas}
          setTotalPages={setTotalPages}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
          setLastAction={setLastAction}
        />
        <SearchMovie 
          setPeliculas={setPeliculas}
          setTotalPages={setTotalPages}
          currentPage={currentPage}
          setCurrentPage={setCurrentPage}
          setLastAction={setLastAction}
        />
      </div>
      <Pagination 
        currentPage={currentPage} 
        totalPages={totalPages} 
        onPageChange={setCurrentPage}
      />
      <MovieList peliculas={peliculas} />
      <Pagination 
        currentPage={currentPage} 
        totalPages={totalPages} 
        onPageChange={setCurrentPage}
      />
    </>
  );
}

export default App;
