import React, { useState, useEffect } from 'react';  // No olvides importar useEffect
import './App.css';
import { MovieList } from './components/MovieList/MovieList'
import { OrderByGenre } from './components/OrderBy/OrderByGenre';
import { SearchMovie } from './components/OrderBy/SearchMovie';
import { Pagination } from './components/Pagination/Pagination';

type Movie = {
  id:number;
  title: string;
  release_date: string;
  poster_path: string;
  overview: string;
};

function App() {
  const [peliculas, setPeliculas] = useState<Movie[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const totalPages = Math.ceil(peliculas.length / 10);

  // Supongamos que tienes una función que determina el modo en que estás (búsqueda o filtrado por género)
  const [mode, setMode] = useState<"search" | "genre" | null>(null);

  useEffect(() => {
    if (mode === "search") {
        fetchMovies();
      // Aquí deberías llamar a la función que hace el fetch de películas basada en la búsqueda.
      // Por ejemplo, suponiendo que tu función se llama fetchSearchMovies:
      // fetchSearchMovies();
    } else if (mode === "genre") {
        fetchGenres();
      // Aquí llamarías a la función que hace el fetch de películas basada en el género.
      // Por ejemplo, suponiendo que tu función se llama fetchGenreMovies:
      // fetchGenreMovies();
    }
  }, [currentPage, mode]);  // Observa cambios en currentPage y en mode

  return (
    <>
      <h1 className='main-title'>Movie Challenge</h1>
      <div className='select-container'>
        <OrderByGenre setPeliculas={setPeliculas} />
        <SearchMovie setPeliculas={setPeliculas} />
      </div>
      <MovieList peliculas={peliculas.slice((currentPage - 1) * 10, currentPage * 10)} />
      <Pagination 
        totalPages={totalPages}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
      />
    </>
  );
}

export default App;
