import React, { useState } from 'react';
import './App.css';
import { MovieList } from './components/MovieList/MovieList'
import { OrderByGenre } from './components/OrderBy/OrderByGenre';
import { SearchMovie } from './components/OrderBy/SearchMovie';
import SortBy from './components/OrderBy/SortBy';

type Movie = {
  id:number;
  title: string;
  release_date: string;
  poster_path: string;
  overview: string;
};

function App() {

  const [peliculas, setPeliculas] = useState<Movie[]>([]);
// setPeliculas-actualizar el estado peliculas del componente padre (App).
  return (
    <>
      <h1 className='main-title'>Movie Challenge</h1>
      <div className='select-container'>
        <OrderByGenre setPeliculas={setPeliculas} /> 
        <SortBy setPeliculas={setPeliculas} />
        <SearchMovie setPeliculas={setPeliculas} />
      </div>
      <MovieList peliculas={peliculas} />
    </>
  ); //renderiza el componente MovieList y le pasa el estado peliculas como prop.
}

export default App;
