import React, { useState } from 'react';
import './App.css';
import { MovieList } from './components/MovieList/MovieList'
import { OrderByGenre } from './components/OrderBy/OrderByGenre';
import { SearchMovie } from './components/OrderBy/SearchMovie';
import SortBy from './components/OrderBy/SortBy';
import Pagination from './components/Pagination/Pagination'; // Importa el componente Pagination


type Movie = {
  id:number;
  title: string;
  release_date: string;
  poster_path: string;
  overview: string;
};

function App() {

  const [peliculas, setPeliculas] = useState<Movie[]>([]);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [totalPages, setTotalPages] = useState<number>(1); // Agrega un estado para las páginas totales

  // Función para actualizar la lista de películas y también las páginas totales
  const updateMovies = (movies: Movie[], total: number) => {
    setPeliculas(movies);
    setTotalPages(total);
  }

  return (
    <>
      <h1 className='main-title'>Movie Challenge</h1>
      <div className='select-container'>
        <OrderByGenre setPeliculas={updateMovies} currentPage={currentPage} />
        <SortBy setPeliculas={updateMovies} />
        <SearchMovie setPeliculas={updateMovies} currentPage={currentPage} />
      </div>
      <MovieList peliculas={peliculas} />
      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={setCurrentPage} /> 
      {/* Agrega el componente Pagination y pasa las propiedades necesarias */}
    </>
  );
}

export default App;
