import React, { useState, useEffect } from 'react';

type Movie = {
  id:number;
  title: string;
  release_date: string;
  poster_path: string;
  overview: string;
};
export const SearchMovie: React.FC<{
  setPeliculas: React.Dispatch<React.SetStateAction<Movie[]>>,
  setTotalPages: React.Dispatch<React.SetStateAction<number>>,
  currentPage: number 
  setCurrentPage: React.Dispatch<React.SetStateAction<number>>
}> = ({ setPeliculas, setTotalPages, currentPage, setCurrentPage }) => {
  const BASE_URL = 'https://api.themoviedb.org/3/search/movie';
  const API_KEY = '03d8479e6ac8e870c3ef0fea7b1b15c3';

  const [busqueda, setBusqueda] = useState<string>('');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setBusqueda(e.target.value);
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setCurrentPage(1);  // Aquí usas setCurrentPage
    fetchMovies();
};


  useEffect(() => {
    if (busqueda) {  // Sólo hace la llamada si hay una búsqueda establecida
      fetchMovies();
    }
  }, [currentPage]);

  const fetchMovies = async () => {
    try {
      const response = await fetch(`${BASE_URL}?query=${busqueda}&api_key=${API_KEY}&page=${currentPage}`);
      const data = await response.json();
      setPeliculas(data.results);
      setTotalPages(data.total_pages);  // Establecer el total de páginas aquí
    } catch (error) {
      console.error("Error fetching movies", error);
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          type='text'
          placeholder='Search by Name'
          value={busqueda}
          onChange={handleInputChange}
        />
        <button type='submit' className='search-button'>
          Search
        </button>
      </form>
    </>
  );
};
